import React from "react";
import { useEffect } from "react";
import { useState } from "react";
import axios from "axios";

const Home = () => {
  const initialState = {
    name: "",
    email: "",
    contact: "",
  };
  const [data, setData] = useState([]);
  const [state, setState] = useState(initialState);
  const loadData = async () => {
    const respnse = await axios.get("http://localhost:3000/api/get");
    setData(respnse.data);
  };
  useEffect(() => {
    loadData();
  }, []);
  console.log({ data });
  const { name, email, contact } = state;
  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:3000/api/post", {
        name,
        email,
        contact,
      })
      .then(() => {
        setState(initialState);
      })
      .catch((error) => console.log(error));
    setTimeout(() => {
      loadData();
    }, 50);
  };
  const handleChange = (e) => {
    const { name, value } = e.target;
    setState({ ...state, [name]: value });
  };
  console.log(state);
  const handleDelete = (id) => {
    axios.delete(`http://localhost:3000/api/remove/${id}`);
    setTimeout(() => {
      loadData();
    }, 50);
  };
  const { id } = useParams();
  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "column", width: "440px" }}
      >
        <input
          type="text"
          name="name"
          value={name}
          placeholder="Name"
          onChange={handleChange}
        />
        <input
          type="text"
          name="email"
          value={email}
          placeholder="Email"
          onChange={handleChange}
        />
        <input
          type="text"
          name="contact"
          value={contact}
          placeholder="Contact"
          onChange={handleChange}
        />
        <input type="submit" value="save" />
      </form>
      <h1>Detail</h1>
      {data.map((item, index) => (
        <div
          key={index}
          style={{ display: "flex", justifyContent: "space-around" }}
        >
          <h3>{item.id}</h3>
          <h3>{item.name}</h3>
          <h3>{item.email}</h3>
          <h3>{item.contact}</h3>
          <button style={{ width: "100px", height: "40px" }}>Edit</button>
          <button
            style={{ width: "100px", height: "40px" }}
            onClick={() => handleDelete(item.id)}
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default Home;
